code.installcode:
function toggle_subs()
{
	global $vbulletin;

	$setting = 0;
	if ($check = $vbulletin->db->query_first("
		SELECT paymentapiid
		FROM " . TABLE_PREFIX . "paymentapi
		WHERE active = 1
	"))
	{
		if ($check = $vbulletin->db->query_first("
			SELECT subscriptionid
			FROM " . TABLE_PREFIX . "subscription
			WHERE active = 1
		"))
		{
			$setting = 1;
		}
	}

	if ($setting != $vbulletin->options['subscriptionmethods'])
	{
		// update $vboptions
		$vbulletin->db->query_write("
			UPDATE " . TABLE_PREFIX . "setting
			SET value = '$setting'
			WHERE varname = 'subscriptionmethods'
		");
		build_options();
	}
}

$db->hide_errors();
$raypaysettings = array(
	"user_id"      => array("type" => "text", "value" => "", "validate" => "string"),
	"marketing_id"      => array("type" => "text", "value" => "", "validate" => "string"),
	"currency_rate"      => array("type" => "text", "value" => "", "validate" => "string"),
	"sandbox"      => array("type" => "yesno", "value" => "0", "validate" => "yesno"),
);

$api = array(
	"title" => "raypay",
	"active" => 1,
	"classname" => "raypay",
	"currency" => "usd",
	"recurring" => 0,
	"settings" => serialize($raypaysettings),
);

$db->query_write(fetch_query_sql($api, 'paymentapi'));
toggle_subs();
$db->show_errors();

code.uninstallcode:
function toggle_subs()
{
	global $vbulletin;


	$setting = 0;
	if ($check = $vbulletin->db->query_first("
		SELECT paymentapiid
		FROM " . TABLE_PREFIX . "paymentapi
		WHERE active = 1
	"))
	{
		if ($check = $vbulletin->db->query_first("
			SELECT subscriptionid
			FROM " . TABLE_PREFIX . "subscription
			WHERE active = 1
		"))
		{
			$setting = 1;
		}
	}

	if ($setting != $vbulletin->options['subscriptionmethods'])
	{
		// update $vboptions
		$vbulletin->db->query_write("
			UPDATE " . TABLE_PREFIX . "setting
			SET value = '$setting'
			WHERE varname = 'subscriptionmethods'
		");
		build_options();
	}
}

$db->hide_errors();
$db->query_write("DELETE FROM " . TABLE_PREFIX . "paymentapi WHERE classname = 'raypay'");
toggle_subs();
$db->show_errors();
